/** {@link https://www.lj-cdn.dev/web-components/scrolling-stat} */

/**
 * Copy Button Web Component
 * @class
 * @extends HTMLElement
 * @classdesc Defines web component that renders a button that copies text to the clipboard.

 *
 */
class CopyButton extends HTMLElement {
  /**
   * Attributes to observe for adding, removing, or changing.
   * @static
   * @returns {string[]} An array of attribute names to observe.
   */
  static get observedAttributes() {
    return [
      "data-size",
      "data-accent-color",
      "data-target-selector",
      "data-title",
    ];
  }

  // constructor
  constructor() {
    super();
    this.attachShadow({ mode: "open" });

    // Create a template for the button
    const template = document.createElement("template");
    template.innerHTML = `
        <style>
          * { box-sizing:border-box;margin:0; }
          button {
            background-color:rgb(100%100%100%/.05);
            border:solid.075em color-mix(in srgb,currentColor 50%,transparent);
            color:currentColor;
            cursor:pointer;
            font-size:var(--size, 16px);
            height:1.75em;
            opacity:.6;
            padding:.3em;
            position:relative;
            width:1.75em;
          }
          button:hover {
            opacity:1;
          }
          button, div {
            align-items:center;
            border-radius:.25em;
            display:flex;
            justify-content:center;
          }
          div {
            background-color:rgb(100%100%100%/.05);
            color: var(--accent-color, blue);
            font-size:.85em;
            letter-spacing:.05em;
            opacity:0;
            padding:.15em .35em;
            position:absolute;
            top:120%;
            transition:opacity .15s;
          }
          svg {
            fill:none;
            height:auto;
            pointer-events:none;
            stroke:currentColor;
            stroke-width:1;
            width:100%;
          }
          path {
            stroke-linecap:round;
            stroke-linejoin:round;
            transition:opacity .15s;
          }
          path#copied {
            opacity:0;
          }
        </style>
        <button>
          <div>copied!</div>
          <svg xmlns='http://www.w3.org/2000/svg' height='20' width='20' viewBox='0 0 24 24'><path style="opacity:1;" id="copy" d='M9 5H7a2 2 0 0 0-2 2v12a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V7a2 2 0 0 0-2-2h-2M9 5a2 2 0 0 0 2 2h2a2 2 0 0 0 2-2M9 5a2 2 0 0 1 2-2h2a2 2 0 0 1 2 2'/><path id="copied" d='M9 5H7a2 2 0 0 0-2 2v12a2 2 0 0 0 2 2h10a2 2 0 0 0 2-2V7a2 2 0 0 0-2-2h-2M9 5a2 2 0 0 0 2 2h2a2 2 0 0 0 2-2M9 5a2 2 0 0 1 2-2h2a2 2 0 0 1 2 2m-6 9 2 2 4-4'/></svg>
        </button>
    `;

    // append the template content to the shadow DOM
    this.shadowRoot?.appendChild(template.content.cloneNode(true));
  }
  /**
   * Method invoked when the custom element is first connected to the document's DOM. Defines DOM elements, adds css styling, and starts the observer.
   * @method
   * @returns {void}
   * @summary Creates the shadow DOM, add styles, and starts the observer.
   */
  connectedCallback() {
    // get selector
    const selector = this.getAttribute("data-target-selector") ?? "";

    // get button
    const button = /** @type {HTMLButtonElement} */ (
      this.shadowRoot?.querySelector("button")
    );

    // get message element
    const message = button?.querySelector("div");

    // get svg paths
    const copy_path = /** @type {SVGPathElement} */ (
      button?.querySelector("#copy")
    );
    const copied_path = /** @type {SVGPathElement} */ (
      button?.querySelector("#copied")
    );

    // get target element
    const target = /** @type {HTMLElement | null | undefined} */ (
      selector ? document.querySelector(selector) : null
    );

    const targetContent = /** @type {string} */ (
      // @ts-expect-error - target might be an input or a textarea
      target?.value ?? target?.textContent ?? ""
    );

    // get accent color
    const accent_color = this.getAttribute("data-accent-color");
    button?.style.setProperty("--accent-color", accent_color ?? "red");

    // get size
    const size = this.getAttribute("data-size");
    button?.style.setProperty("--size", size ?? "16px");

    // get title
    const title = this.getAttribute("data-title");
    button?.setAttribute("title", title ?? "copy to clipboard");

    // helper function to reveal / hide the copied icon
    const showCopied = () => {
      // reveal copied icon
      if (copy_path && copied_path && message) {
        button.style.opacity = "1";
        button.style.color = "var(--accent-color)";
        copy_path.style.opacity = "0";
        copied_path.style.opacity = "1";
        message.style.opacity = "1";
      }
    };
    function hideCopied() {
      // reveal copied icon
      if (copy_path && copied_path && message) {
        button.style.removeProperty("opacity");
        button.style.removeProperty("color");
        copy_path.style.opacity = "1";
        copied_path.style.opacity = "0";
        message.style.opacity = "0";
      }
    }
    /**
     * @type {string | number | NodeJS.Timeout | undefined}
     */
    let timeout;
    button?.addEventListener("click", () => {
      // copy text to clipboard
      if (target && targetContent) {
        // clear timeout if it exists
        if (timeout) clearTimeout(timeout);
        navigator.clipboard
          .writeText(targetContent)
          .then(() => {
            // show copied icon
            showCopied();

            // hide copied icon after 2 seconds
            timeout = setTimeout(() => {
              hideCopied();
              clearTimeout(timeout);
            }, 1500);
          })
          .catch((err) => console.error("failed to copy text: ", err));
      } else {
        console.error("copy failed");
      }
    });
  }
}
customElements.define("copy-button", CopyButton);
export default CopyButton;

/** @copyright 2024 Lightning Jar - "Scrolling Stat" web component - License MIT */
/** @author Kevin Peckham */
/** @license MIT */
/** @version 0.0.1 */
/** {@link https://www.lj-cdn.dev/web-components/scrolling-stat} */

/**
 * Scrolling Stat Web Component
 * @class
 * @extends HTMLElement
 * @classdesc Defines web component that displays a number that counts up when it is scrolled into view.
 * @attribute {string} [data-duration=800] The duration of the animation in milliseconds.
 * @attribute {string} [data-number-start=0] The starting number.
 * @attribute {string} [data-number-end=99] The ending number.
 * @attribute {string} [data-unit=""] The unit of measurement.
 * @attribute {string} [data-text=""] The text to display below the number.
 *
 */
class ScrollingStat extends HTMLElement {
  isOnScreen = false;
  start = 0;
  numberSpan = document.createElement("span");

  /**
   * Attributes to observe for adding, removing, or changing.
   * @static
   * @returns {string[]} An array of attribute names to observe.
   */
  static get observedAttributes() {
    return [
      "data-duration",
      "data-number-start",
      "data-number-end",
      "data-unit",
      "data-text",
    ];
  }

  // constructor
  constructor() {
    super();

    // binding the parent context to the methods
    this.connectedCallback = this.connectedCallback.bind(this);
    this.tween = this.tween.bind(this);
    this.observerCallback = this.observerCallback.bind(this);
  }
  /**
   * Method invoked when the custom element is first connected to the document's DOM. Defines DOM elements, adds css styling, and starts the observer.
   * @method
   * @returns {void}
   * @summary Creates the shadow DOM, add styles, and starts the observer.
   */
  connectedCallback() {
    // create a shadow root
    const shadowRoot = this.attachShadow({ mode: "open" }),
      elem = document.createElement("span"),
      unitElem = document.createElement("span"),
      textElem = document.createElement("span"),
      styleElem = document.createElement("style"),
      attrs = ["data-text", "data-unit"].map(
        (attr) => this.getAttribute(attr) || "",
      );

    // add text content
    this.numberSpan.textContent = "0";
    [textElem.textContent, unitElem.textContent] = attrs;

    // add the style rules
    styleElem.textContent = `
    * { margin:0;font-size;1em;box-sizing:border-box; }
    #w {
      align-items:baseline;
      background:rgb(100%100%100%/5%);
      border-radius:.3em;
      display:grid;
      font-weight:800;
      gap:.3em;
      grid:auto-flow/auto 1fr;
      line-height:1;
      outline:1px solid;
      padding:1.5em;
      max-width:12em;
    }
    #w span:nth-child(1) {
      font-size:2.75em;
    }
    #w span:nth-child(2) {
      font-size:1.9em;
    }
    #w span:nth-child(3) {
      font-size:.9em;
      font-style:italic;
      font-weight:400;
      grid-column:1/span 2;
      line-height:1.3;
      opacity:.9;
    }`;

    // append to shadow DOM
    shadowRoot.replaceChildren(styleElem, elem);
    elem.replaceChildren(this.numberSpan, unitElem, textElem);
    elem.id = "w";

    // create the observer
    const observer = new IntersectionObserver(this.observerCallback, {
      rootMargin: "0%",
      threshold: 0.5,
    });

    // start the observer
    observer.observe(elem);
  }
  /**
   * @param {IntersectionObserverEntry[]} entries
   * @returns {void}
   * @description callback method which fires when the element is scrolled into view
   */
  observerCallback(entries) {
    // test if the element is on screen
    const isVisible = entries[0].isIntersecting;

    // if on screen and not already on screen, start the animation
    if (isVisible != this.isOnScreen) {
      this.start = 0;
      this.tween();
      this.isOnScreen = isVisible;
    }
  }
  /**
   * Tween Method
   * @method
   * @returns {void}
   * @example
   * tween(0, 99)
   */
  tween() {
    // record the start time
    if (!this.start) this.start = Date.now();

    // get the duration
    const duration = Math.abs(
        parseInt(this.getAttribute("data-duration") || "800"),
      ),
      // get the end value
      endValue = parseFloat(this.getAttribute("data-number-end") || "99"),
      // get the start value
      // @ts-expect-error -- parseFloat will coerce the string to a number
      // prettier-ignore
      startValue = parseFloat(this.getAttribute("data-number-start") || (Math.abs(endValue) > 49 ? 0 : endValue > 0 ? 99 : -99)),
      // get progress
      progress = Math.min((Date.now() - this.start) / duration, 1),
      // get the current value
      currentValue = startValue + (endValue - startValue) * progress,
      // decimal places -- limit to 2
      decimalPlaces = Math.min((`${endValue}`.split(".")[1] || "").length, 2);

    // advance the displayed number
    this.numberSpan.textContent = currentValue.toFixed(decimalPlaces);

    // if the current number is not yet the target number, request another frame
    Date.now() - this.start < duration &&
    endValue - Number(this.numberSpan.textContent)
      ? window.requestAnimationFrame(() => this.tween())
      : // set the final number
        (this.numberSpan.textContent = endValue.toFixed(decimalPlaces));
  }
}
customElements.define("scrolling-stat", ScrollingStat);
export default ScrollingStat;
